const initialState = {
    showUserForm: false,
    addUser: false,
    users: []
}

const Reducer = (state = initialState, action = '') => {
    switch(action.type) {
        case 'user_Form':
            return Object.assign({}, state, { showUserForm: true });
        case 'ADD_USER':
            const user = {
                'name': action.name,
                'email': action.email,
            };
            const users = [...state, { users: state.users.push(user) }]
            return Object.assign({}, state, { users: state.users , addUser: true });
        default:
            return state;
    }
}

export default Reducer
