const initialState = {
    showUserForm: false,
    users: []
}

const Reducer = (state = initialState, action = '') => {
    switch(action.type) {
        case 'user_Form':
            return Object.assign({}, state, { showUserForm: true });
        case 'ADD_USER':
            return Object.assign({}, state, { users: [ ...state.users, {
                'id': users.length + 1,
                'name': action.name,
                'email': action.email,
            } ] });
