const initialState = {
    showUserForm: false
}

const Reducer = (state = initialState, action = '') => {
    switch(action.type) {
        case 'user_Form':
            return Object.assign({}, state, { showUserForm: true });
        case 'ADD_USER':
            return state;
        default:
            return state;
    }
}

export default Reducer
