const initialState = {
    showUserForm: false,
    users: []
}

const Reducer = (state = initialState, action = '') => {
    switch(action.type) {
        case 'user_Form':
            return Object.assign({}, state, { showUserForm: true });
        case 'ADD_USER':
            state.user.push({
                'name': action.name,
                'email': action.email,
            });
            return state;
        default:
            return state;
    }
}

export default Reducer
